//  swap assign test
//  Tests: String a("hello");
//         String b("world");
//         a = b;
//         assert(a == "world");
//         assert(b == "world");
//

#include "string.hpp"
#include <cassert>
#include <iostream>
 
//===========================================================================
int main (){
    {
        //------------------------------------------------------
        // SETUP FIXTURE
        String a("hello");
        String b("world");
        // TEST
        a = b;
        // VERIFY
        assert(a == "world");
        assert(b == "world");    
    }
    {
        //------------------------------------------------------
        // SETUP FIXTURE
        String a("This string will change");
        String b("This string does not change");
        // TEST
        a = b;
        // VERIFY
        assert(a == "This string does not change");
        assert(b == "This string does not change");    
    }
    {
        //------------------------------------------------------
        // SETUP FIXTURE
        String a("123456789012345678901234567890");
        String b("qwertyuiopasdfghjklzxcvbnmqwertyuiopasdfghjklzxcvbnm");
        // TEST
        a = b;
        // VERIFY
        assert(a == "qwertyuiopasdfghjklzxcvbnmqwertyuiopasdfghjklzxcvbnm");
        assert(b == "qwertyuiopasdfghjklzxcvbnmqwertyuiopasdfghjklzxcvbnm");    
    }
    {
        //------------------------------------------------------
        // SETUP FIXTURE
        String a("test");
        String b;
        // TEST
        a = b;
        // VERIFY
        assert(a == "");
        assert(b == "");    
    }
    {
        //------------------------------------------------------
        // SETUP FIXTURE
        String a;
        String b("testing the string");
        // TEST
        a = b;
        // VERIFY
        assert(a == "testing the string");
        assert(b == "testing the string");    
    }
    {
        //------------------------------------------------------
        // SETUP FIXTURE
        String a("T                          E                       S                       T");
        String b("S                          T                       R                       I                       N                      G");
        // TEST
        a = b;
        // VERIFY
        assert(a == "S                          T                       R                       I                       N                      G");
        assert(b == "S                          T                       R                       I                       N                      G");    
    }
    {
        //------------------------------------------------------
        // SETUP FIXTURE
        String a("");
        String b("");
        // TEST
        a = b;
        // VERIFY
        assert(a == "");
        assert(b == "");    
    }
    {
        //------------------------------------------------------
        // SETUP FIXTURE
        String a("!@#$%^&*()!@#$%^&*()!@#$%^&*()");
        String b(")(*&^%$#@!)(*&^%$#@!)(*&^%$#@!");
        // TEST
        a = b;
        // VERIFY
        assert(a == ")(*&^%$#@!)(*&^%$#@!)(*&^%$#@!");
        assert(b == ")(*&^%$#@!)(*&^%$#@!)(*&^%$#@!");    
    }
    
    std::cout << "---> TESTING COMPLETE FOR SWAP ASSIGN <---" << std::endl;
    return 0;
}
